INSERT INTO books (name, price, publication_year, description) VALUES ('Fables and Legends', 19.99, TIMESTAMP WITH TIME ZONE '2008-07-03', 'Discover a treasury of timeless fables and legendary tales passed down through generations. Each story carries wisdom, moral lessons, and echoes of cultural heritage, enchanting readers with narratives that transcend time.')
